package com.geebles.chap01;enum CoffeeSize { 	SMALL(8, "Brown"), TALL(12, "Brown"), GRANDE(16, "Brown") {		public String getLidCode() {			return "Medium";		}	}	, VENTI(20, "Mahogany") {		public String getLidCode() {			return "Large";		}	};	private int ounces;	private String cupColor;	public String getLidCode() {		return "Normal";	}		CoffeeSize(int ounces) {		this.ounces = ounces;	}	CoffeeSize(int ounces, String cupColor) {		this.ounces = ounces;		this.cupColor = cupColor;	}		public String getCupColor() {		return cupColor;	}		public int getOunces() {		return ounces;	} } public class Coffee {	enum CoffeeType { CORSICIAN, HAZELNUT, DECAF } 	CoffeeSize size;	CoffeeType type;	public static void main (String... args) {		Coffee c = new Coffee();		c.size = CoffeeSize.TALL;		System.out.println(c.size.getOunces());				System.out.println("----");				c.type = Coffee.CoffeeType.DECAF;				for (CoffeeSize cs : CoffeeSize.values()) {			System.out.println(cs + " " + cs.getOunces() + " " + cs.getLidCode() + " " + cs.getCupColor());		}				for (CoffeeType ct : CoffeeType.values()) {			System.out.println(ct);		}	}}