package com.geebles.chap01;enum CoffeeSize { 	SMALL(8), TALL(12), GRANDE(16), VENTI(20);	private int ounces;		CoffeeSize(int ounces) {		this.ounces = ounces;	}		public int getOunces() {		return ounces;	} } public class Coffee {	enum CoffeeType { CORSICIAN, HAZELNUT, DECAF } 	CoffeeSize size;	CoffeeType type;	public static void main (String... args) {		Coffee c = new Coffee();		c.size = CoffeeSize.TALL;		System.out.println(c.size.getOunces());				System.out.println("----");				c.type = Coffee.CoffeeType.DECAF;				for (CoffeeSize cs : CoffeeSize.values()) {			System.out.println(cs + " " + cs.getOunces());		}				for (CoffeeType ct : CoffeeType.values()) {			System.out.println(ct);		}	}}